long long strToInt(const string &s) {
    long long res = 0;
    for (auto e : s) res = res * 10 + e - '0';
    return res;
}

long long calcDiff(const string &s, const string &news) {
    const int L = s.length();
    long long snum = strToInt(s), newsnum = strToInt(news);
    return min<long long>(llabs(snum - newsnum), powl(10, L) - llabs(snum - newsnum));
}

int main(void) {
    string s;
    cin >> s;
    vector<int> v(10);
    iota(begin(v), end(v), 0);
    string ans = s;
    const int L = s.length();
    int diff = 0;
    do {
        string news = "";
        for (int i = 0; i < L; i++) {
            news += v[i] + '0';
        }
        const int buf = calcDiff(s, news);
        if (diff < buf) {
            ans = news;
            diff = buf;
        } else if (diff == buf) {
            const long long newsnum = strToInt(news);
            const long long ansnum = strToInt(ans);
            if (newsnum < ansnum) {
                ans = news;
                diff = buf;
            }
        }
    } while (next_permutation(begin(v), end(v)));
    cout << ans << endl;
}

