LL convToLL(string str)
{
  LL ret = 0;
  for (size_t i = 0; i < str.length(); i++) {
    ret *= 10;
    ret += str[i] - '0';
  }
  return ret;
}

string convToString(LL num, int k)
{
  string ret;
  while(num){
    ret += (num % 10) + '0';
    num /= 10;
  }
  while(ret.length() < k){
    ret += '0';
  }

  reverse(begin(ret), end(ret));

  return ret;
}

bool comp(const string &A, const string &B)
{
  if(A == ""){
    return false;
  }
  else if(B == ""){
    return true;
  }
  else{
    LL AA = convToLL(A), BB = convToLL(B);
    LL tmpS = convToLL(S);
    if(min(abs(AA - tmpS), (LL)pow((LL)10, S.length()) - (LL)abs(AA - tmpS)) >
    min(abs(BB - tmpS), (LL)pow((LL)10, S.length()) -(LL)abs(BB - tmpS))){
      return false;
    }
    else if(min(abs(BB - tmpS), (LL)pow((LL)10, S.length()) - (LL)abs(BB - tmpS)) >
  min(abs(AA - tmpS), (LL)pow((LL)10, S.length()) - (LL)abs(AA - tmpS))){
      return true;
    }
    else{
      if(A < B){
        return true;
      }
      else{
        return false;
      }
    }
  }
}

