void saiki(int idx,ll num) {
	if (idx == N) {
		if (ma < min(abs(n - num), (ll)pow(10, N) - abs(n - num))) {
			ma = max(ma, min(abs(n - num), (ll)pow(10, N) - abs(n - num)));
