}

pair<int32_t, string> min_str(string A, vector<bool> used, string B)
{
  if (A.size() == B.size()) {
    return {diff(A, B), B};
  } else {
    vector<pair<int32_t, string>> strs;
    for (uint32_t i = 0; i < used.size(); i++) {
      if (!used[i]) {
        used[i] = true;
        string b = string(B);
        b += i + '0';
        strs.push_back(min_str(A, used, b));
        used[i] = false;
      }
    }
    sort(strs.begin(), strs.end());
    if (strs.size() > 0) {
      return strs[strs.size() - 1];
    } else {
      return {-1, ""};
    }
  }
}

