vector<int> all;
int pow_10;

void dfs(int m, int bit, int sum) {
  if ( m == n ) {
    if ( abs(sum-t) < pow_10/3 ) return;
    all.emplace_back(sum);
    return;
  }

  for ( int i = 0; i < 10; i++ ) {
    if ( bit & (1<<i) ) continue;
    int nxt = sum;
    nxt *= 10;
    nxt += i;
    dfs(m+1, bit|(1<<i), nxt);    
  }
}

struct St {
  int m, bit, sum;  
};

signed main() {
