  ios_base::sync_with_stdio(0);
  cout << fixed << setprecision(12);

  cin >> s;
  t = stoi(s);

  n = s.size();
  pow_10 = pow(10, n);  
  // dfs(0, 0, 0);

  stack<St> S;
  S.push({0, 0, 0});

  while ( !S.empty() ) {
    St p = S.top(); S.pop();
    int m = p.m, bit = p.bit, sum = p.sum;    
    if ( m == n ) {
      if ( abs(sum-t) < pow_10/3 ) continue;      
      all.emplace_back(sum);
      continue;      
    }

    for ( int i = 0; i < 10; i++ ) {
      if ( bit & (1<<i) ) continue;
      int nxt = sum;
      nxt *= 10;
      nxt += i;
      // dfs(m+1, bit|(1<<i), nxt);      
      S.push({m+1, bit|(1<<i), nxt});      
    }
  }

  sort(all.begin(), all.end());  

  int ans = t;
  int ma = 0;
  for ( int i = 0; i < (int)all.size(); i++ ) {
