  i64 v = 0;
  for (i32 i = 0; i < len; ++i) {
    v = 10 * v + s[i] - '0';
  }
  i32 a[10];
  for (i32 i = 0; i < f; ++i) {
    a[i] = i;
  }
  max_diff = 0;
  max_val = 1000000000000;
  calc (0, a, v, len);
  i64 p = 1;
  for (i32 i = 1; i < len; ++i) {
    p *= 10;
  }
  if (max_val < p) putchar ('0');
  printf ("%" PRIi64 "\n", max_val);
}

int main (void) {
  run();
  return 0;
}

