inline std::vector<std::string> split(const std::string &str, char sep)
{
    std::vector<std::string> v;
    std::stringstream ss(str);
    std::string buffer;
    while( std::getline(ss, buffer, sep) ) {
        v.push_back(buffer);
    }
    return v;
}

int w = 0,h = 0;

inline string make_jfen(bool *ball){

    string ret = "";
    bool flag =false;
    for(int j=0; j<w; j++){
        if(ball[j]){
            flag = true;
            break;
        }
    }
    if(!flag){
        stringstream ss;
        ss << w;
        char num_c;
        ss >> num_c;
        ret += num_c;
    }
    else if(flag){
        int counter = 0;
        for(int j=0; j<w; j++){
            if(ball[j]){
                if(counter != 0){
                    stringstream ss;
                    ss << counter;
                    char num_c;
                    ss >> num_c;
                    ret += num_c;
                    counter = 0;
                }
                ret += 'b';
            }
            else if(!ball[j]){
                counter++;
            }
        }
        if(counter != 0){
            stringstream ss;
            ss << counter;
            char num_c;
            ss >> num_c;
            ret += num_c;
            counter = 0;
        }
    }
    return ret;

}

