int main() {

    string s;
    while (cin >> s, s != "#") {

        ll a, b, c, d;
        cin >> a >> b >> c >> d;

        vector<string> tbl = split(s);
        REP(i, tbl.size()) tbl[i] = parse(tbl[i]);

        tbl[a-1][b-1] = '.';
        tbl[c-1][d-1] = 'b';

        REP(i, tbl.size()) tbl[i] = jfenize(tbl[i]);

        ll h = tbl.size();
        REP(i, h) cout << tbl[i] << "/\n"[i == h-1];

    }

}

