class Solve {
public:

    vector<vector<char>> decode(string s) {
        vector<vector<char>> ret(1);
        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == '/') {
                ret.push_back(vector<char>());
            } else if (s[i] == 'b') {
                ret.back().push_back('b');
            } else {
                rep(j, '0', s[i]) {
                    ret.back().push_back('.');
                }
            }
        }
        return ret;
    }

    string encode(vector<vector<char>> vv) {
        string ret;
        for (int i = 0; i < vv.size(); ++i) {
            for (int j = 0; j < vv[i].size(); ++j) {
                if (vv[i][j] == 'b') {
                    ret.push_back('b');
                } else {
                    if (!ret.size() || ret.back() == 'b' || ret.back() == '/') {
                        ret.push_back('1');
                    } else {
                        ret.back()++;
                    }
                }
            }
            if (i + 1 < vv.size()) {
                ret += '/';
            }
        }
        return ret;
    }

    bool solve() {
        string s;
        cin >> s;
        if (s == "#") return false;
        Int a, b, c, d;
        cin >> a >> b >> c >> d;
        a--, b--, c--, d--;
        auto tapu = decode(s);
        tapu[a][b] = '.';
        tapu[c][d] = 'b';
        string tapi = encode(tapu);
        cout << tapi << endl;

        return true;
    }
};


