vector<string> Split(const string& s, char sep) {
	vector<int> pos{ -1 };
	rep(i, sz(s)) {
		if (s[i] == sep)pos.push_back(i);
	}
	pos.push_back(sz(s));

	vector<string> res;
	rep(i, sz(pos) - 1) {
		res.push_back(s.substr(pos[i] + 1, pos[i + 1] - pos[i] - 1));
	}
	return res;
}

template<class T, class U = typename T::value_type>auto RLE(const T& a) {
	int n = a.size();
	vector<int> res1; vector<U> res2;
	for (int i = 0; i < n;) {
		int cnt = 0; auto e = a[i];
		for (; i < n && a[i] == e; ++i)++cnt;
		res1.push_back(cnt);
		res2.push_back(e);
	}
	return make_pair(res1, res2);
}

string to_grid(const string& s) {
	string res;
	for (char c : s) {
		if (isdigit(c)) {
			res += string(c - '0', '.');
		} else {
			res += 'b';
		}
	}
	return res;
}
string to_str(const string& s) {
	VI cnt; vector<char> chr;
	tie(cnt, chr) = RLE(s);
	string res;
	rep(i, sz(cnt)) {
		if (chr[i] == '.') {
			res += cnt[i] + '0';
		} else {
			res += string(cnt[i], 'b');
		}
	}
	return res;
}

int main() {
	for (string S = in; S != "#"; in(S)) {
		VS s = Split(S, '/');
		VS grid = Map(s, [&](const string& s) {return to_grid(s); });
		INT(a, b, c, d);
		a--; b--; c--; d--;
		grid[a][b] = '.';
		grid[c][d] = 'b';
		VS str = Map(grid, [&](const string& s) {return to_str(s); });
		out.set(DivStr("/", "\n"))(str);
	}
}

