VS decomp(string s){
  int h = 1, w = 0;
  int n = SZ(s);
  for(int i=0;i<n;++i) if(s[i] == '/') ++h;
  for(int i=0;i<n&&s[i]!='/';++i){
	if(s[i] == 'b') ++w;
	else w += s[i] - '0';
  }

  VS res(h, string(w, '.'));
  int y = 0, x = 0;
  for(int i=0;i<n;++i){
	if(s[i] == '/'){
	  ++y;
	  x = 0;
	}
	else if(s[i] == 'b'){
	  res[y][x++] = 'b';
	}
	else{
	  x += s[i] - '0';
	}
  }
  return res;
}
string comp(VS& vs){
  int h = SZ(vs);
  int w = SZ(vs[0]);
  string res;
  for(int y=0;y<h;++y){
	int acc = 0;
	for(int x=0;x<w;++x){
	  if(vs[y][x] == '.') ++acc;
	  else{
		if(acc) res += to_string(acc);
		res += "b";
		acc = 0;
	  }
	}
	if(acc)
	  res += to_string(acc);
	if(y < h-1) res += "/";
  }
  return res;
}

