vector<vector<char>> toGrid(string s) {
  vector<vector<char>> grid;
  s = s + '/';
  int n = s.size();
  vector<char> add;
  for (int i = 0; i < n; i++) {
    if (s[i] == '/') {
      grid.push_back(add);
      add.resize(0);
    } else {
      if (s[i] == 'b') {
        add.push_back('b');
      } else {
        int cnt = s[i] - '0';
        while (cnt--) 
          add.push_back('.');
      }
    }
  }

  return grid;
}

string toJfan(vector<vector<char>> grid) {
  int h = grid.size();
  int w = grid[0].size();
  string ret = "";
  for (int i = 0; i < h; i++) {
    int cnt = 0;
    for (int j = 0; j < w; j++) {
      if (grid[i][j] == 'b') {
        if (cnt > 0) {
          ret += (char)(cnt + '0');
        }
        ret += 'b';
        cnt = 0;
      } else cnt++;
    }
    if (cnt > 0) {
      ret += (char)(cnt + '0');
    }

    if (i != h - 1) {
      ret += '/';
    }
  }

  return ret;
}

