vector<vector<int>> jtof(string S) {
    S.push_back('/');
    vector<vector<int>> ret;
    vector<int> tmp;
    for (int i = 0; i < S.length(); i++) {
        if (S[i] == 'b') {
            tmp.push_back(0);
        } else if (S[i] == '/') {
            ret.push_back(tmp);
            tmp.clear();
        } else {
            int num = (int)(S[i] - '0');
            for (int j = 0; j < num; j++) {
                tmp.push_back(1);
            }
        }
    }
    return ret;
}

string ftoj(vector<vector<int>> field) {
    string ret;
    for (int i = 0; i < field.size(); i++) {
        int cnt = 0;
        for (int j = 0; j < field[i].size(); j++) {
            if (field[i][j] == 1) {
                cnt++;
            } else if (field[i][j] == 0){
                if (cnt) ret.push_back('0' + cnt);
                cnt = 0;
                ret.push_back('b');
            }
            if (field[i].size() - 1 == j) {
                if (cnt) ret.push_back('0' + cnt);
                ret.push_back('/');
            }
        }
    }
    return ret;
}


string solve(string S) {
    vector<vector<int>> field = jtof(S);
    int a, b, c, d; cin >> a >> b >> c >> d;
    a--, b--, c--, d--;
    field[a][b] = 1;
    field[c][d] = 0;
    string ret = ftoj(field);
    return ret;
}

