vector<string> jfen2table(const string &jfen) {
    string jeffen = jfen + "/";
    vector<string> table;
    string q;
    for (const char &c : jeffen) {
        if (c == '/') {
            table.push_back(q);
            q = "";
        } else if (c == 'b') {
            q += 'b';
        } else {
            q += string(c - '0', '.');
        }
    }
    return table;
}

string table2jfen(const vector<string> &table) {
    string jfen = "";
    for (const string &line : table) {
        for (const char &c : line) {
            if (c == '.') {
                if (!jfen.empty() && '1' <= jfen.back() && jfen.back() < '9') {
                    jfen.back()++;
                } else {
                    jfen += "1";
                }
            } else {
                jfen += "b";
            }
        }
        jfen += "/";
    }
    jfen.pop_back();
    return jfen;
}

