vector<string> toV(string s){
  string x;
  vector<string> res;
  FOR(i,0,s.size()){
    if(s[i]=='b'){
      x.push_back('b');
    }
    else if(s[i]=='/'){
      res.push_back(x);
      string inst;
      x=inst;
    }
    else{
      ll p = s[i] - '0';
      FOR(i,0,p){
        x.push_back('.');
      }
    }
  }
  res.push_back(x);
  return res;
}

string toS(vector<string> v){
  ll m = v[0].size();
  ll n = v.size();
  string res;
  FOR(i,0,n){
    char x='0';
    FOR(j,0,m){
      if(v[i][j]=='b'){
        if(x!='0')res.push_back(x);
        res.push_back('b');
        x = '0';
      }
      else if(v[i][j]=='.'){
        x++;
      }
    }
    if(x!='0')res.push_back(x);
    x='0';
    if(i!=n-1)res.push_back('/');
  }
  return res;
}

