string jfen(vector<string> board) {
    int n = sz(board);
    string ret;
    rep(i, 0,n) {
        int cnt = 0;
        string line;
        rep(j, 0, sz(board[i])) {
            if (board[i][j] == '.') {
                cnt++;
            } else {
                if (cnt) line += cnt + '0';
                cnt = 0;
                line += 'b';
            }
        }
        if (cnt) line += cnt + '0';
        ret += line;
        if (i != n - 1) ret += '/';
    }
    return ret;
}
vector<string> unjfen(string s) {
    vector<string> ret;
    auto to_line = [](string line) {
        string r;
        rep(i, 0, sz(line)) {
            if (line[i] == 'b') {
                r += "#";
            } else {
                r += string(line[i] - '0', '.');
            }
        }
        return r;
    };
    s += "/";
    string tmp;
    rep(i, 0, sz(s)) {
        if (s[i] == '/') {
            ret.push_back(to_line(tmp));
            tmp = "";
        } else {
            tmp += s[i];
        }
    }
    return ret;
}

