ll sq(ll num) { return num*num; }
ll mod_pow(ll x, ll n) {
	if (n == 0)return 1;
	if (n == 1)return x%MOD;
	ll res = sq(mod_pow(x, n / 2));
	res %= MOD;
	if (n % 2 == 1) {
		res *= x;
		res %= MOD;
	}
	return res;
}
ll mod_add(ll a, ll b) { return (a + b) % MOD; }
ll mod_sub(ll a, ll b) { return (a - b + MOD) % MOD; }
ll mod_mul(ll a, ll b) { return a*b % MOD; }

vector<vector<bool>> cht(string s) {
	vector<vector<bool>> res;
	vector<bool> buf;
	rep(i, s.size()) {
		if (s[i] == 'b')buf.push_back(1);
		else if (s[i] == '/') {
			res.push_back(buf);
			buf.clear();
		}
		else {
			rep(j, s[i] - '0')buf.push_back(0);
		}
	}
	res.push_back(buf);
	return res;
}

string chs(vector<vector<bool>> t) {
	string res;
	rep(i, t.size()) {
		ll cnt = 0;
		rep(j, t[i].size()) {
			if (t[i][j]) {
				string ss = "0";
				if (cnt != 0) {
					ss[0] += cnt;
					res += ss;
				}
				res += "b";
				cnt = 0;
			}
			else cnt++;
		}
		string ss = "0";
		if (cnt != 0) {
			ss[0] += cnt;
			res += ss;
		}
		if (i != t.size() - 1)res += "/";
	}
	return res;
}

