vector<string> getBoard(string s) {
    vector<string> t;
    stringstream ss(s);
    string line;
    while (getline(ss, line, '/')) {
        string temp = "";
        for (auto c : line) {
            if (c == 'b') {
                temp += 'b';
            } else {
                rep(i, c - '0') temp += '.';
            }
        }
        t.emplace_back(temp);
    }
    return t;
}

string make(vector<string> t) {
    string s = "";
    int cnt = 0;
    for (auto l : t) {
        cnt++;
        int n = 0;
        for (auto c : l) {
            if (c == 'b') {
                if (n > 0) {
                    s += n + '0';
                    n = 0;
                }
                s += 'b';
            } else {
                n++;
            }
        }
        if (n > 0) {
            s += n + '0';
            n = 0;
        }
        if (cnt < t.size()) s += '/';
    }

    return s;
}

