V<string> fromjfen(string S) {
    S += "/";
    V<string> ret;
    string sub;
    for (char c : S) {
        if (c == '/') {
            ret.pb(sub);
            sub = "";
        } else if (c == 'b') {
            sub += "b";
        } else {
            sub += string(c - '0', '.');
        }
    }
    return ret;
}

string tojfen(V<string> field) {
    string ret;

    for (string line : field) {
        line += "b";
        ll cnt = 0;
        for (char c : line) {
            if (c == 'b') {
                if (cnt > 0) {
                    ret += to_string(cnt);
                    cnt = 0;
                }
                ret += "b";
            } else {
                ++cnt;
            }
        }

        ret.pop_back();
        ret += "/";
    }

    ret.pop_back();
    return ret;
}

void solve() {
    while (true) {
        string S;
        cin >> S;
        if (S == "#") break;

        V<string> field = fromjfen(S);
        cout << field << endl;
        ll a, b, c, d;
        cin >> a >> b >> c >> d;

        field[a - 1][b - 1] = '.';
        field[c - 1][d - 1] = 'b';

        cout << tojfen(field) << endl;
    }
    return;
}

