/*--------------------template--------------------*/

vs dec(string s)
{
	vs res;
	string t;
	for (auto c : s)
	{
		if (c == '/')
		{
			res.push_back(t);
			t.clear();
		}
		else if (c == 'b')
		{
			t.push_back('b');
		}
		else if (isdigit(c))
		{
			REP(i, c - '0') t.push_back('.');
		}
		else assert(false);
	}
	res.push_back(t);
	return res;
}

string enc(string s)
{
	string res;
	int cnt = 0;
	for (auto c : s)
	{
		if (c == '.') cnt++;
		else
		{
			if (cnt != 0) res.push_back('0' + cnt);
			res.push_back('b');
			cnt = 0;
		}
	}		
	if (cnt != 0) res.push_back('0' + cnt);
	return res;
}

