		cin >> a >> b >> c >> d;
		a--; b--; c--; d--;
		vs fld = dec(s);
		fld[a][b] = '.';
		fld[c][d] = 'b';
		REP(i, fld.size()) cout << enc(fld[i]) << (i == fld.size() - 1 ? "\n" : "/");
	}
	return 0;
}

