using B = vector<string>;

B hoge(string &s){
    int cnt = 0;
    for(char c: s) cnt += (c == '/');
    B ret(cnt+1);
    int i = 0;
    for(char c: s){
        if(c == '/'){
            ++i;
            continue;
        }
        if(c == 'b') ret[i] += "b";
        else{
            rep(_, c-'0') ret[i] += ".";
        }
    }
    return ret;
}

string fuga(B &a){
    string s;
    rep(i, a.size()){
        int l = 0;
        while(l < a[i].size()){
            if(a[i][l] == 'b'){
                s += "b";
                ++l;
                continue;
            }
            int r = l;
            while(r < a[i].size() && a[i][l] == a[i][r]) ++r;
            s += '0' + (r-l);
            l = r;
        }
        if(i < a.size()-1) s += "/";
    }
    return s;
}

int main(){
    string s;
    while(cin >> s && s != "#"){
        int a, b, c, d;
        scanf("%d%d%d%d", &a, &b, &c, &d);
        auto tmp = hoge(s);
        tmp[a-1][b-1] = '.';
        tmp[c-1][d-1] = 'b';
        cout << fuga(tmp) << "\n";
    }
}

