                }
                stringstream ss;
                ss << cc;
                int tmp;
                ss >> tmp;
                index += tmp;
            }
            insert_ball[d] = true;
            vec[c] = make_jfen(insert_ball);
        }
        else if(a == c){

            memset(avoid_ball, false, sizeof(avoid_ball));
