vector<vector<int>>getJfen(string s){
	int W = 0;
	H = 0;
	rep(i,0,s.size()){
		if(s[i]=='/')break;
		if(s[i]=='b')W++;
		else W+=s[i]-'0';
	}
	rep(i,0,s.size())if(s[i]=='/')H++;
	vector<vector<int>>mat(H+1,vector<int>(W,0));
	int tate = 0,yoko = 0;
	rep(i,0,s.size()){
		if(s[i]=='b')mat[tate][yoko++] = 1;
		else if(s[i]=='/'){
			yoko=0;
			tate++;
		}else{
			yoko+=s[i]-'0';
		}
	}
	return mat;
}

