string board[10];

//jfenを元の記号列に戻す
string decode(string in)
{
	string ret;
	for(int i=0;i<in.size();i++)
	{
		if(in[i]=='b')
			ret+='b';
		else{
			int n=in[i]-'0';
			for(int j=0;j<n;j++)
				ret+='.';
		}
	}
	return ret;
}

//記号列をjfenに直す
string encode(string in)
{
	string ret;
	int ind=0;
	while(ind<in.size())
	{
		//cerr<<ind<<endl;
		if(in[ind]=='b')
		{
			ret+='b';
			ind++;
		}else{
			int cnt=0;
			while(ind<in.size() && in[ind]=='.')
			{
				cnt++;
				ind++;
			}
			ret+=(char)('0'+cnt);
		}
	}
	return ret;
}

