int main(void) {
	char S[1024];
	int a, b, c, d;
	while (scanf("%s", S) == 1 && S[0] != '#') {
		int W = -1, H = -1;
		char board[16][16] = {{0}};
		int i, x = 0, y = 0;
		int count;
		if (scanf("%d%d%d%d", &a, &b, &c, &d) != 4) return 1;
		/* decode */
		for (i = 0; S[i] != '\0'; i++) {
			if (S[i] == 'b') {
				board[y][x++] = 'b';
			} else if (S[i] == '/') {
				if (W < 0) W = x;
				y++;
				x = 0;
			} else {
				x += S[i] - '0';
			}
		}
		/* move */
		H = y + 1;
		board[a - 1][b - 1] = 0;
		board[c - 1][d - 1] = 'b';
		/* encode */
		for (y = 0; y < H; y++) {
			count = 0;
			for (x = 0; x < W; x++) {
				if (board[y][x] == 'b') {
					if (count > 0) printf("%d", count);
					putchar('b');
					count = 0;
				} else {
					count++;
				}
			}
			if (count > 0) printf("%d", count);
			putchar(y + 1 < H ? '/' : '\n');
		}
	}
	return 0;
}

import java.util.*;

public class Main {
	public static void main (String[] args) {
		Scanner sc = new Scanner(System.in);
		StringBuilder sb = new StringBuilder();
		while (true) {
		    String s = sc.next();
		    if (s.equals("#")) {
		        break;
		    }
		    String[] arr = s.split("/");
		    int h = arr.length;
		    int w = getSize(arr[0]);
		    boolean[][] field = new boolean[h][];
		    for (int i = 0; i < h; i++) {
		        field[i] = getArray(arr[i], w);
		    }
		    field[sc.nextInt() - 1][sc.nextInt() - 1] = false;
		    field[sc.nextInt() - 1][sc.nextInt() - 1] = true;
		    for (int i = 0; i < h; i++) {
		        if (i > 0) {
		            sb.append("/");
		        }
		        sb.append(getString(field[i]));
		    }
		    sb.append("\n");
		}
		System.out.print(sb);
	}

	static String getString(boolean[] arr) {
	    StringBuilder sb = new StringBuilder();
	    int count = 0;
	    for (boolean b : arr) {
	        if (b) {
	            if (count > 0) {
	                sb.append(count);
	                count = 0;
	            }
	            sb.append("b");
	        } else {
	            count++;
	        }
	    }
	    if (count > 0) {
	        sb.append(count);
	    }
	    return sb.toString();
	}

	static int getSize(String s) {
	    int ans = 0;
	    for (char c : s.toCharArray()) {
	        if (c == 'b') {
	            ans++;
	        } else {
	            ans += c - '0';
	        }
	    }
	    return ans;
	}

	static boolean[] getArray(String s, int length) {
	    boolean[] ans = new boolean[length];
	    int idx = 0;
	    for (char c : s.toCharArray()) {
	        if (c == 'b') {
	            ans[idx] = true;
	            idx++;
	        } else {
	            idx += c - '0';
	        }
	    }
	    return ans;
	}
}

import java.util.ArrayList;
import java.util.Scanner;

public class Main {

    static int W = 0;
    static int H = 0;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        while (true) {
            String s = sc.next();
            if ("#".equals(s)) {
                break;
            }
            int y1 = sc.nextInt() - 1;
            int x1 = sc.nextInt() - 1;
            int y2 = sc.nextInt() - 1;
            int x2 = sc.nextInt() - 1;
            int[][] map = parse(s);
            // arrayDump(map);
            map[y1][x1] = 0;
            map[y2][x2] = 1;
            // arrayDump(map);
            System.out.println(serialize(map));
        }
    }

    public static void arrayDump(int[][] a) {
        for (int i = 0, l = a.length; i < l; i++) {
            for (int j = 0, l2 = a[0].length; j < l2; j++) {
                System.out.print(a[i][j] + " ");
            }
            System.out.println();
        }
        System.out.println();
    }

    public static int[][] parse(String s) {
        String[] lines = s.split("/");
        ArrayList<ArrayList<Integer>> map = new ArrayList<>();
        for (String line : lines) {
            ArrayList<Integer> l = new ArrayList<>();
            for (int i = 0; i < line.length(); i++) {
                char c = line.charAt(i);
                if ('0' <= c && c <= '9') {
                    int n = Integer.valueOf(c + "");
                    for (int j = 0; j < n; j++) {
                        l.add(0);
                    }
                } else {
                    l.add(1);
                }
            }
            map.add(l);
        }
        int[][] mm = new int[lines.length][map.get(0).size()];
        for (int i = 0; i < lines.length; i++) {
            for (int j = 0; j < map.get(0).size(); j++) {
                mm[i][j] = map.get(i).get(j);
            }
        }
        return mm;
    }

    public static String serialize(int[][] a) {
        String[] lines = new String[a.length];
        for (int i = 0; i < a.length; i++) {
            String line = "";
            int k = 0;
            for (int j = 0; j < a[0].length; j++) {
                if (a[i][j] == 0) {
                    k++;
                } else {
                    if (k > 0) {
                        line += String.valueOf(k);
                        k = 0;
                    }
                    line += "b";
                }
            }
            if (k > 0) {
                line += String.valueOf(k);
            }
            lines[i] = line;
        }
        return String.join("/", lines);
    }
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.NoSuchElementException;

public class Main {
	String S;
	String[] split;
	Character[][] board;
	int a,b,c,d;
	public void solve() {
		while(true){
			S = next();

			if(S.equals("#"))break;

			split = S.split("/");
			a = nextInt() - 1;
			b = nextInt() - 1;
			c = nextInt() - 1;
			d = nextInt() - 1;

			board = new Character[split.length][];
			for(int i = 0;i < split.length;i++){
				char[] chars = split[i].toCharArray();
				ArrayList<Character> list = new ArrayList<Character>();
				for(char c : chars){
					if(c == 'b'){
						list.add('b');
					}else{
						for(int j = 0;j < c - '0';j++){
							list.add('.');
						}
					}
				}
				board[i] = (Character[])list.toArray(new Character[0]);
			}

			board[a][b] = '.';
			board[c][d] = 'b';

			StringBuilder sb = new StringBuilder();
			for(int i = 0;i < split.length;i++){
				if(i != 0)sb.append("/");
				int j = 0;
				for(;j < board[i].length;){
					if(board[i][j] == 'b'){
						sb.append(board[i][j]);
						j++;
					}else{
						int cnt = 0;
						while(j < board[i].length && board[i][j] == '.'){
							cnt++;
							j++;
						}
						sb.append(cnt);
					}
				}
			}
			out.println(sb);
		}
	}
	public static void main(String[] args) {
		out.flush();
		new Main().solve();
		out.close();
	}

	/* Input */
	private static final InputStream in = System.in;
	private static final PrintWriter out = new PrintWriter(System.out);
	private final byte[] buffer = new byte[2048];
	private int p = 0;
	private int buflen = 0;

	private boolean hasNextByte() {
		if (p < buflen)
			return true;
		p = 0;
		try {
			buflen = in.read(buffer);
		} catch (IOException e) {
			e.printStackTrace();
		}
		if (buflen <= 0)
			return false;
		return true;
	}

	public boolean hasNext() {
		while (hasNextByte() && !isPrint(buffer[p])) {
			p++;
		}
		return hasNextByte();
	}

	private boolean isPrint(int ch) {
		if (ch >= '!' && ch <= '~')
			return true;
		return false;
	}

	private int nextByte() {
		if (!hasNextByte())
			return -1;
		return buffer[p++];
	}

	public String next() {
		if (!hasNext())
			throw new NoSuchElementException();
		StringBuilder sb = new StringBuilder();
		int b = -1;
		while (isPrint((b = nextByte()))) {
			sb.appendCodePoint(b);
		}
		return sb.toString();
	}

	public int nextInt() {
		return Integer.parseInt(next());
	}

	public long nextLong() {
		return Long.parseLong(next());
	}

	public double nextDouble() {
		return Double.parseDouble(next());
	}
}

