namespace Aizu {
    public class OnlineJudge {
        static void Main() {
            var output = new StringBuilder();
            for (var input = string.Empty; (input = Console.ReadLine()) != "#";) {
                var lines = input.Split('/');
                var board = Enumerable.Range(0, lines.Length)
                    .Select(i =>
                        string.Join("b",
                            lines[i]
                                .Split('b')
                                .Select(q => new String('.', string.IsNullOrEmpty(q) ? 0 : int.Parse(q)))
                                .ToArray())
                        .ToCharArray())
                    .ToArray();

                var command = Console.ReadLine().Split().Select(q => int.Parse(q) - 1).ToArray();
                board[command[0]][command[1]] = '.';
                board[command[2]][command[3]] = 'b';

                for (int i = 0; i < lines.Length; ++i) {
                    for (int j = 0; j < board[i].Length;) {
                        var block = board[i].Skip(j).TakeWhile(c => c == board[i][j]).ToArray();
                        j += block.Length;
                        output.Append(block.First() == '.' ? block.Length.ToString() : new string(block));
                    }

                    output.Append('/');
                }

                output.Length -= 1;
                output.AppendLine();
            }

            Console.Write(output.ToString());
        }
    }
}

