    ret = []
    for row in rows:
        r = []
        c = 0
        for s in row:
            if s == "b":
                if c:
                    r.append(str(c))
                r.append(s)
                c = 0
            else:
                c += 1
        if c:
            r.append(str(c))
        ret.append(''.join(r))
    return '/'.join(ret)


