vector<int> solve(){
    vector<int> res;
    int H,W; cin >> H >> W;
    vector<vector<int>> D(H,vector<int>(W));
    for(auto& vec:D) for(auto& in:vec) cin >> in;
    vector<vector<int>> couho(H,vector<int>(W,0));
    for(int i = 0; i < H; i++){
        for(int j = 0; j < W;j++){
            if(D[i][j] == 0) continue;
            if(D[i][j] != 0){
                int cost = D[i][j];
                for(int k = 0; k < 3;k++){
                    for(int l = 0; l < 3;l++){
                        D[i+k][j+l] -= cost;
                    }
                }
                couho[i+1][j+1]++;
            }
        }
    }
    [&](){
        for(int i = H-1; i >= 0; i--){
            for(int j = 0; j < W;j++){
                if(couho[i][j]){
                    res.push_back(i);
                    res.push_back(j);
                    return;
                }
            }
        }
    }();
    [&](){
        for(int i = 0; i < H; i++){
            for(int j = W-1; j >= 0;j--){
                if(couho[i][j]){
                    res.push_back(i);
                    res.push_back(j);
                    return;
                }
            }
        }
    }();
    return res;
}
int main(void) {
    cin.tie(0); ios_base::sync_with_stdio(false);
    cout << solve() << endl;
    return 0;
}

