	vector<vector<int>>v(H,vector<int>(W));
	for(auto &i:v)for(auto &j:i)cin>>j;
	vector<vector<int>>visited(H,vector<int>(W));
	for(int i=0;i<H-2;i++){
		for(int j=0;j<W-2;j++){
			while(v[i][j]){
				visited[i+1][j+1]++;
				for(int k=0;k<3;k++){
					for(int l=0;l<3;l++){
						v[i+k][j+l]--;
					}
				}
			}
		}
	}
	vector<pair<int,int>>two;
	vector<pair<int,int>>one;
	for(int i=0;i<H;i++){
		for(int j=0;j<W;j++){
			if(!visited[i][j])continue;
			if(visited[i][j]==1){
				int sum=0;
				for(int k=-1;k<=1;k++){
					for(int l=-1;l<=1;l++){
						sum+=visited[i+k][j+l];
					}
				}
				if(sum<3){
					one.push_back({i,j});
				}
			}
			else{
				two.push_back({i,j});
			}
		}
	}
	vector<pair<int,int>>ans;
