vector<int> solve(){
    vector<int> res;
    int H,W; cin >> H >> W;
    vector<vector<int>> D(H,vector<int>(W));
    for(auto& vec:D) for(auto& in:vec) cin >> in;
    vector<vector<int>> couho(H,vector<int>(W,0));
    for(int i = 0; i < H; i++){
        for(int j = 0; j < W;j++){
            if(D[i][j] == 0) continue;
            if(D[i][j] != 0){
                int cost = D[i][j];
                for(int k = 0; k < 3;k++){
                    for(int l = 0; l < 3;l++){
                        D[i+k][j+l] -= cost;
                    }
                }
                couho[i+1][j+1]++;
            }
        }
    }
    vector<pii> loc;
    for(int i = 0; i < H;i++){
        for(int j = 0; j < W;j++){
            if(couho[i][j]){
                int cnt = 0;
                for(int k = -1; k <= 1; k++){
                    for(int l = -1; l <= 1; l++){
                        if(couho[i+k][j+l]) cnt++;
                    }
                }
                if(cnt <= 2){
                    loc.push_back({i,j});
                }
            }
        }
    }
    sort(loc.rbegin(),loc.rend());
    res = vector<int>{loc.begin()->first,loc.begin()->second,loc.rbegin()->first,loc.rbegin()->second};
    return res;
}
int main(void) {
    cin.tie(0); ios_base::sync_with_stdio(false);
    cout << solve() << endl;
    return 0;
}

