#include <bits/stdc++.h>
using namespace std;

const int dx[] = {-1, 0, 1, 0, -1, -1, 1, 1};
const int dy[] = {0, -1, 0, 1, -1, 1, -1, 1};
vector<int> up = {0, 4, 5};

using Tp = tuple<int, int>;

int h, w;
int d[500][500];
int dist[500][500];

void bfs(int sx, int sy)
{
	fill_n(*dist, 500 * 500, -9999);

	queue<Tp> q;

	dist[sx][sy] = 0;
	q.push(Tp(sx, sy));

	for (; q.size();){
		int x, y;
		tie(x, y) = q.front(); q.pop();

		for (int dir = 0; dir < 8; dir++){
			int nx = x + dx[dir];
			int ny = y + dy[dir];
			if (nx < 0 || ny < 0 || nx >= h || ny >= w) continue;
			if (d[nx][ny] == 0) continue;
			if (dist[nx][ny] != -9999) continue;
			dist[nx][ny] = dist[x][y] + 1;
			q.push(Tp(nx, ny));
		}
	}
}

int main()
{
	scanf("%d %d", &h, &w);
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			scanf("%d", &d[i][j]);
		}
	}

	int tx, ty;
	for (int i = 1; i + 1 < h; i++){
		for (int j = 1; j + 1 < w; j++){
			int t = 9999;
			for (int dir : up){
				int nx = i + dx[dir];
				int ny = j + dy[dir];
				t = min(t, d[nx][ny]);
			}
			for (int dir = 0; dir < 8; dir++){
				int nx = i + dx[dir];
				int ny = j + dy[dir];
				d[nx][ny] -= t;
			}
			if (t != 0){
				tx = i;
				ty = j;
			}
		}
	}

	bfs(tx, ty);
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			if (dist[i][j] > dist[tx][ty]){
				tx = i;
				ty = j;
			}
		}
	}
	bfs(tx, ty);
	int sx = 0, sy = 0;
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			if (dist[i][j] > dist[sx][sy]){
				sx = i;
				sy = j;
			}
		}
	}

	if (Tp(sx, sy) < Tp(tx, ty)){
		swap(sx, tx);
		swap(sy, ty);
	}
	printf("%d %d %d %d\n", sx, sy, tx, ty);
}

