template<typename ...> static inline int getchar_unlocked(void) { return getchar(); }
template<typename ...> static inline void putchar_unlocked(int c) { putchar(c); }
#define mygc(c) (c)=getchar_unlocked()
#define mypc(c) putchar_unlocked(c)
void reader(int& x) { int k, m = 0; x = 0; for (;;) { mygc(k); if (k == '-') { m = 1; break; }if ('0' <= k&&k <= '9') { x = k - '0'; break; } }for (;;) { mygc(k); if (k<'0' || k>'9')break; x = x * 10 + k - '0'; }if (m) x = -x; }
void reader(ll& x) { int k, m = 0; x = 0; for (;;) { mygc(k); if (k == '-') { m = 1; break; }if ('0' <= k&&k <= '9') { x = k - '0'; break; } }for (;;) { mygc(k); if (k<'0' || k>'9')break; x = x * 10 + k - '0'; }if (m) x = -x; }
int reader(char c[]) { int i, s = 0; for (;;) { mygc(i); if (i != ' '&&i != '\n'&&i != '\r'&&i != '\t'&&i != EOF) break; }c[s++] = i; for (;;) { mygc(i); if (i == ' ' || i == '\n' || i == '\r' || i == '\t' || i == EOF) break; c[s++] = i; }c[s] = '\0'; return s; }
template <class T, class S> void reader(T& x, S& y) { reader(x); reader(y); }
template <class T, class S, class U> void reader(T& x, S& y, U& z) { reader(x); reader(y); reader(z); }
template <class T, class S, class U, class V> void reader(T& x, S& y, U& z, V & w) { reader(x); reader(y); reader(z); reader(w); }
void writer(int x, char c) { int s = 0, m = 0; char f[10]; if (x<0)m = 1, x = -x; while (x)f[s++] = x % 10, x /= 10; if (!s)f[s++] = 0; if (m)mypc('-'); while (s--)mypc(f[s] + '0'); mypc(c); }
void writer(ll x, char c) { int s = 0, m = 0; char f[20]; if (x<0)m = 1, x = -x; while (x)f[s++] = x % 10, x /= 10; if (!s)f[s++] = 0; if (m)mypc('-'); while (s--)mypc(f[s] + '0'); mypc(c); }
void writer(const char c[]) { int i; for (i = 0; c[i] != '\0'; i++)mypc(c[i]); }
void writer(const char x[], char c) { int i; for (i = 0; x[i] != '\0'; i++)mypc(x[i]); mypc(c); }
template<class T> void writerLn(T x) { writer(x, '\n'); }
template<class T, class S> void writerLn(T x, S y) { writer(x, ' '); writer(y, '\n'); }
template<class T, class S, class U> void writerLn(T x, S y, U z) { writer(x, ' '); writer(y, ' '); writer(z, '\n'); }
template<class T> void writerArr(T x[], int n) { if (!n) { mypc('\n'); return; }FOR(i, n - 1)writer(x[i], ' '); writer(x[n - 1], '\n'); }

ll mod_pow(ll a, ll n, ll mod) { ll ret = 1; ll p = a % mod; while (n) { if (n & 1) ret = ret * p % mod; p = p * p % mod; n >>= 1; } return ret; }
template<class T> T extgcd(T a, T b, T& x, T& y) { for (T u = y = 1, v = x = 0; a;) { T q = b / a; swap(x -= q * u, u); swap(y -= q * v, v); swap(b -= q * a, a); } return b; }
template<class T> T mod_inv(T a, T m) { T x, y; extgcd(a, m, x, y); return (m + x % m) % m; }
template<class T> T gcd(T a, T b) { return b ? gcd(b, a % b) : a; }

int n, m;
int a[500][500];

int dx[] = { 1, 1, -1, -1 };
int dy[] = { 1, -1, 1, -1 };

int check(int x, int y) {
	if (a[x][y] == 0) return -1;
	int idx = -1;
	FOR(i, 4) {
		int nx = x + dx[i], ny = y + dy[i];
		if (nx < 0 || nx >= n || ny < 0 || ny >= m) continue;
		if (a[nx][ny] == 0) continue;
		if (idx == -1) idx = i;
		else return -1;
	}
	return idx;
}

void erase(int x, int y) {
	for (int a = -1; a <= 1; a++) {
		for (int b = -1; b <= 1; b++) {
			::a[x + a][y + b]--;
		}
	}
}

int main() {
	reader(n, m);
	FOR(i, n) FOR(j, m) reader(a[i][j]);
	vector<Pii> vv;
	bool updated = true;
	while (updated) {
		updated = false;
		FOR(i, n) {
			FOR(j, m) {
				int c = check(i, j);
				if (c != -1) {
					vv.push_back(Pii(i + dx[c], j + dy[c]));
					erase(i + dx[c], j + dy[c]);
					updated = true;
					break;
				}
			}
			if (updated)break;
		}
	}
	vector<int> cnt(sz(vv));
	FOR(i, sz(vv)) for (int j = i + 1; j < sz(vv); j++) {
		int dx = vv[i].first - vv[j].first;
		int dy = vv[i].second- vv[j].second;
		if (abs(dx) <= 1 && abs(dy) <= 1) {
			cnt[i]++; cnt[j]++;
		}
	}

	vector<Pii> ans;
	FOR(i, sz(vv)) if (cnt[i] == 1) ans.push_back(vv[i]);
	if (sz(ans) == 0) ans.push_back(vv[0]), ans.push_back(vv[0]);
	sort(ans.begin(), ans.end());
	vv = ans;

	printf("%d %d %d %d\n", vv[1].first, vv[1].second, vv[0].first, vv[0].second);

	return 0;
}

