		rep(k, 3) rep(l, 3) if(a[i - 1 + k][j - 1 + l]) cnt++;
		if(cnt <= 2) ans.pb(mp(i - 1, j - 1));
	}
	sort(all(ans), greater<pi>());
	if(ans.empty()){
		for(int i = 1; i <= h; i++) for(int j = 1; j <= w; j++){
			int s = 0;
			rep(k, 3) rep(l, 3) s += in[i - 1 + k][j - 1 + k];
			if(s == 2 * 9) ans.pb(mp(i - 1, j - 1));
		}
		ans.pb(ans.back());
		assert(ans.size() == 2);
	}
	rep(i, ans.size()) cout << ans[i].first<<" "<<ans[i].second<<(i+1==ans.size()?"\n":" ");

	return 0;
}

