int H,W;
int total;

int t[600][600];


bool dfs(int y,int x,int depth,int pi=10,int pj=10){
  if(y==0||x==0||y==H||x==W)return false;

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      if(t[y+i][x+j]==0)return false;

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      t[y+i][x+j]--;


  if(depth*9==total){
    cout<<y<<' '<<x<<' ';
    return true;
  }

  for(int i=-1;i<=1;i++){
    for(int j=-1;j<=1;j++){
      if(depth*9+27<=total &&  i==-pi&&  j==-pj )continue;

      if(dfs(y+i,x+j,depth+1,i,j))return true;
    }
  }

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      t[y+i][x+j]++;

  return false;
}
