bool dfs(int y,int x,int depth){
  if(y==0||x==0||y==H||x==W)return false;

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      if(t[y+i][x+j]==0)return false;

  for(int i=-1;i<=1;i++)
