#define MOD 1000000007
#define EPS 0.000000001
using namespace std;

struct Info{
	Info(int arg_row,int arg_col){
		row = arg_row;
		col = arg_col;
	}
	int row,col;
};

int H,W;
int table[500][500],calc[500][500],adj_count[500][500];
int diff_row[8] = {-1,-1,-1,0,0,1,1,1},diff_col[8] = {-1,0,1,-1,1,-1,0,1};


bool rangeCheck(int row,int col){
	if(row >= 0 && row <= H-1 && col >= 0 && col <= W-1)return true;
	else{
		return false;
	}
}

int main(){

	scanf("%d %d",&H,&W);
	for(int row = 0; row < H; row++){
		for(int col = 0; col < W; col++)scanf("%d",&table[row][col]);
	}

	for(int row = 0; row < H; row++){
		for(int col = 0; col < W; col++){
			calc[row][col] = 0;
		}
	}

	int adj_sum,adj_row,adj_col;

	for(int row = 0; row <= H-2; row++){
		for(int col = 0; col <= W-2; col++){

			adj_sum = 0;
			for(int i = 0; i < 8; i++){
				adj_row = row+diff_row[i];
				adj_col = col+diff_col[i];
				if(rangeCheck(adj_row,adj_col) == false)continue;

				adj_sum += calc[adj_row][adj_col];
			}

			calc[row+1][col+1] = table[row][col]-(calc[row][col]+adj_sum);
		}
	}

	for(int row = 0; row < H; row++){
		for(int col = 0; col < W; col++)adj_count[row][col] = 0;
	}

	vector<Info> ANS;
	int count;

	for(int row = 0; row < H; row++){
		for(int col = 0; col < W; col++){
			if(calc[row][col] == 0)continue;

			count = 0;
			for(int i = 0; i < 8; i++){
				adj_row = row+diff_row[i];
				adj_col = col+diff_col[i];

				if(rangeCheck(adj_row,adj_col) == false || calc[adj_row][adj_col] == 0)continue;

				count++;
				if(count >= 2)break;
			}
			if(count <= 1)ANS.push_back(Info(row,col));
		}
	}

	if(ANS.size() == 1){
		printf("%d %d %d %d\n",ANS[0].row,ANS[0].col,ANS[0].row,ANS[0].col);
		return 0;
	}

	if(10000*ANS[1].row+ANS[1].col > 10000*ANS[0].row+ANS[0].col)swap(ANS[0],ANS[1]);

	printf("%d %d %d %d\n",ANS[0].row,ANS[0].col,ANS[1].row,ANS[1].col);

	return 0;
}

