			if (abs(dx) == 1 && abs(dy) == 1) {
				auto a = vp[i], b = vp[j];
				vp.clear();
				vp.push_back(a); vp.push_back(b);
				break;
			}
		}
	}
	sort(vp.begin(), vp.end());

	printf("%d %d %d %d\n",vp[1].first,vp[1].second,vp[0].first,vp[0].second);

	return 0;
}

#include<bits/stdc++.h>
using namespace std;

int H,W;
int total;

int t[600][600];


bool dfs(int y,int x,int depth,int pi=10,int pj=10){
  if(y==0||x==0||y==H||x==W)return false;

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      if(t[y+i][x+j]==0)return false;

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      t[y+i][x+j]--;


  if(depth*9==total){
    cout<<y<<' '<<x<<' ';
    return true;
  }

  for(int i=-1;i<=1;i++){
    for(int j=-1;j<=1;j++){
      if(depth>=3 && ( i==-pi || j==-pj) )continue;
      if(dfs(y+i,x+j,depth+1,i,j))return true;
    }
  }

  for(int i=-1;i<=1;i++)
    for(int j=-1;j<=1;j++)
      t[y+i][x+j]++;

  return false;
}
int main(){
  cin>>H>>W;
  for(int i=0;i<H;i++){
    for(int j=0;j<W;j++){
      cin>>t[i][j];
      total+=t[i][j];
    }
  }


  for(int i=0;i<H;i++){
    for(int j=0;j<W;j++){

      if(dfs(i,j,1)){
        cout<<i<<' '<<j<<endl;
        return 0;
      }

    }
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int dx[] = {-1, 0, 1, 0, -1, -1, 1, 1};
const int dy[] = {0, -1, 0, 1, -1, 1, -1, 1};
vector<int> up = {0, 4, 5};

typedef tuple<int, int> Tp;

int h, w;
int d[500][500];
int dist[500][500];

void bfs(int sx, int sy)
{
	fill_n(*dist, 500 * 500, -9999);

	queue<Tp> q;

	dist[sx][sy] = 0;
	q.push(Tp(sx, sy));

	for (; q.size();){
		int x, y;
		tie(x, y) = q.front(); q.pop();

		for (int dir = 0; dir < 8; dir++){
			int nx = x + dx[dir];
			int ny = y + dy[dir];
			if (nx < 0 || ny < 0 || nx >= h || ny >= w) continue;
			if (d[nx][ny] == 0) continue;
			if (dist[nx][ny] != -9999) continue;
			dist[nx][ny] = dist[x][y] + 1;
			q.push(Tp(nx, ny));
		}
	}
}

int main()
{
	scanf("%d %d", &h, &w);
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			scanf("%d", &d[i][j]);
		}
	}

	int tx, ty;
	for (int i = 1; i + 1 < h; i++){
		for (int j = 1; j + 1 < w; j++){
			int t = 9999;
			for (int dir : up){
				int nx = i + dx[dir];
				int ny = j + dy[dir];
				t = min(t, d[nx][ny]);
			}
			for (int dir = 0; dir < 8; dir++){
				int nx = i + dx[dir];
				int ny = j + dy[dir];
				d[nx][ny] -= t;
			}
			if (t != 0){
				tx = i;
				ty = j;
			}
		}
	}

	/*for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			if (d[i][j] == 2){
				printf("%d %d %d %d\n", i, j, i, j);
				return 0;
			}
		}
	}*/

	bfs(tx, ty);
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			if (dist[i][j] > dist[tx][ty]){
				tx = i;
				ty = j;
			}
		}
	}
	bfs(tx, ty);
	int sx = 0, sy = 0;
	for (int i = 0; i < h; i++){
		for (int j = 0; j < w; j++){
			if (dist[i][j] > dist[sx][sy]){
				sx = i;
				sy = j;
			}
		}
	}

	if (Tp(sx, sy) < Tp(tx, ty)){
		swap(sx, tx);
		swap(sy, ty);
	}

	printf("%d %d %d %d\n", sx, sy, tx, ty);
}

