#define rep(i,n) for(int i=0;i<(int)n;i++)
#define all(c) (c).begin(),(c).end()
#define mp make_pair
#define pb push_back
#define each(i,c) for(__typeof((c).begin()) i=(c).begin();i!=(c).end();i++)
#define dbg(x) cerr<<__LINE__<<": "<<#x<<" = "<<(x)<<endl

using namespace std;

typedef long long ll;
typedef vector<int> vi;
typedef pair<int,int> pi;
const int inf = (int)1e9;
const double INF = 1e12, EPS = 1e-9;

int h, w, in[502][502], a[502][502];
void add(int y, int x){
	rep(i, 3) rep(j, 3) in[y - 1 + i][x - 1 + j]--;
	a[y][x]++;
}
int main(){
	cin.tie(0); cin.sync_with_stdio(0);
	cin >> h >> w;
	rep(i, h) rep(j, w) cin >> in[i + 1][j + 1];
	while(1){
		bool update = 0;
		for(int i = 1; i <= h; i++) for(int j = 1; j <= w; j++){
			if(in[i][j] + in[i][j + 1] + in[i + 1][j] + in[i + 1][j + 1] != 1) continue;
			if(in[i][j] == 1)              add(i - 1, j - 1), update = 1;
			else if(in[i][j + 1] == 1)     add(i - 1, j + 2), update = 1;
			else if(in[i + 1][j] == 1)     add(i + 2, j - 1), update = 1;
			else if(in[i + 1][j + 1] == 1) add(i + 2, j + 2), update = 1;
		}
		if(!update) break;
	}
	//cerr<<endl;rep(i, h+2) rep(j, w+2) cerr<<a[i][j]<<(j==w+1?"\n":" "); 

	vector<pi> ans;

	for(int i = 1; i <= h; i++) for(int j = 1; j <= w; j++) if(a[i][j] == 1){
		int cnt = 0;
		rep(k, 3) rep(l, 3) if(a[i - 1 + k][j - 1 + l]) cnt++;
		if(cnt <= 2) ans.pb(mp(i - 1, j - 1));
	}
	sort(all(ans), greater<pi>());
	if(ans.empty()){
		for(int i = 1; i <= h; i++) for(int j = 1; j <= w; j++){
			int s = 0;
			rep(k, 3) rep(l, 3) s += in[i - 1 + k][j - 1 + k];
			if(s == 2 * 9) ans.pb(mp(i - 1, j - 1));
		}
		ans.pb(ans.back());
		assert(ans.size() == 2);
	}
	else assert(ans.size() == 2);
	rep(i, ans.size()) cout << ans[i].first<<" "<<ans[i].second<<(i+1==ans.size()?"\n":" ");

	return 0;
}

#include <bits/stdc++.h>
using namespace std;


int H,W;
int D[500][500];
int LIM[500][500];
int O[500][500];
int T[500][500];
int chmin(int &a,int b){
	if( a > b ) a = b;
}


int put(int i,int j){
	for(int k = -1 ; k <= 1 ; k++){
		for(int l = -1 ; l <= 1 ; l++){
			if(i+k < 0 || j+l < 0 || i+k >= H || j+l >= W ) continue;
			D[i+k][j+l]--;
		}
	}
}
int main(){
	cin >> H >> W;
	for(int i = 0 ; i < H ; i++){
		for(int j = 0 ; j < W ; j++){
			cin >> D[i][j];
			LIM[i][j] = 1e9;
		}
	}

	for(int i = 0 ; i < H ; i++){
		for(int j = 0 ; j < W ; j++){
			while( D[i][j] ){
				put(i+1,j+1);
				O[i+1][j+1] = 1;
			}
		}
	}
	for(int i = 0 ; i < H ; i++){
		for(int j = 0 ; j < W ; j++){
			if( O[i][j] )
			for(int k = -1 ; k <= 1 ; k++){
				for(int l = -1 ; l <= 1 ; l++){
					if( k==0&&l==0||i+k < 0 || j+l < 0 || i+k >= H || j+l >= W ) continue;
					T[i+k][j+l]++;
				}
			}
		}
	}
	vector< pair<int,int> > s;
	for(int i = 0 ; i < H ; i++){
		for(int j = 0 ; j < W ; j++){
			if( O[i][j] && T[i][j] <= 1 )
				s.push_back({i,j});
		}
	}
	if( s.size() == 1 ){
		s.push_back(s[0]);
	}
	assert(s.size()==2);
	sort(s.rbegin(),s.rend());
	cout << s[0].first << " " << s[0].second << " " << s[1].first << " " << s[1].second << endl;
}

