string solve(vector<vs> hs)
{
	vi ps(2);
	rep(i,hs[0].size()){
		rep(j,2){
			if(hs[j][i]==kougekida && ps[j]==0)
				if(!(ps[!j]==0 && hs[!j][i]==kougekida))
					return winner[!j];
		}
		rep(j,2){
			if(hs[j][i]==tameru){
