        }
    }

    vector<int> attack(2, 0);
    string ans = "Hikiwake-kun";
    for(int i=0; i<n; ++i){
        // 反則
        if(pose[0][i] == "kougekida" && attack[0] == 0){
            if(!(pose[1][i] == "kougekida" && attack[1] == 0)){
                ans = "Nakajima-kun";
                break;
            }
