            if(pose[1][i] == "mamoru"){
                /* nothing */
            }
            else if(pose[1][i] == "tameru"){
                attack[1] = min(attack[1] + 1, 5);
            }
            else{
                if(attack[1] == 5){
                    ans = "Nakajima-kun";
                    break;
                }
                attack[1] = 0;
            }
        }
        else if(pose[0][i] == "tameru"){
            if(pose[1][i] == "mamoru"){
                attack[0] = min(attack[0] + 1, 5);
            }
            else if(pose[1][i] == "tameru"){
                attack[0] = min(attack[0] + 1, 5);
                attack[1] = min(attack[1] + 1, 5);
            }
            else{
                ans = "Nakajima-kun";
                break;
            }
        }
        else{
            if(pose[1][i] == "mamoru"){
                if(attack[0] == 5){
                    ans = "Isono-kun";
                    break;
                }
                attack[0] = 0;
            }
            else if(pose[1][i] == "tameru"){
                ans = "Isono-kun";
                break;
            }
            else{
                if(attack[0] < attack[1]){
                    ans = "Nakajima-kun";
                    break;
                }
                else if(attack[1] < attack[0]){
                    ans = "Isono-kun";
                    break;
                }
                attack[0] = 0;
                attack[1] = 0;
            }
        }
    }
    cout << ans << endl;

    return 0;
}

