            }
            if(iso[i]=="mamoru") {
                if(pow2==5) return "Nakajima-kun";
            }
            pow2=0;
        }
        if(naka[i]=="kougekida") pow2=0;
        if(iso[i]=="kougekida") pow1=0;
    }

    return "Hikiwake-kun";
}
