#include<bits/stdc++.h>
using namespace std;

bool judge(string a, string b, int Apow, int Bpow){
  if(a == "kougekida"){
    if(Apow == 0)return false;
    if(Apow >= 5)return true;
    if(b == "tameru")return true;
    if(b == "mamoru")return false;
    if(b == "kougekida")return Apow > Bpow;
  }
  if(b == "kougekida" && Bpow == 0)return true;
  return false;
}

void update(string a, int &Apow){
  if(a == "kougekida")Apow = 0;
  if(a == "tameru")Apow++;
}

int main(){
  int n;
  cin >> n;
  vector<string> a(n),b(n);
  for(int i=0;i<n;i++)cin >> a[i];
  for(int i=0;i<n;i++)cin >> b[i];

  int res = 0, Apow = 0, Bpow = 0;
  for(int i=0;i<n;i++){
    bool Awin = judge(a[i],b[i],Apow,Bpow);
    bool Bwin = judge(b[i],a[i],Bpow,Apow);
    if(Awin != Bwin){
      res = (Awin ? 1 : -1);
      break;
    }
    update(a[i],Apow);
    update(b[i],Bpow);
  }
  cout << (res ? (res>0 ? "Isono-kun" : "Nakajima-kun") : "Hikiwake-kun") << endl;
}

