    } 
    if (iso[i] == 1) {
      if (naka[i] == 0) res = snaka;
      if (iHP < 5) iHP++;
    } 
    if (iso[i] == 0) {
      if (naka[i] == 0 && nHP < iHP) res = siso;
      else if (naka[i] == 0 && nHP > iHP) res = snaka;
      else if (naka[i] != 0 && iHP == 0) res = snaka;
      else if (naka[i] != 0 && iHP == 5) res = siso;
      else if (naka[i] == 1)res = snaka;
      iHP = 0;
    }

    if (naka[i] == 0) nHP = 0;
    c = i;
  }

  cout << res << endl;

}

