using namespace std;

#define BLK 0
#define CHG 1
#define ATK 2

int n, opr[2][111];

void compute() {
  int power[2] = {0,0};
  rep(i,n) {
    if( opr[0][i] == ATK || opr[1][i] == ATK ) {
      if( opr[0][i] == ATK && opr[1][i] == ATK ) {
	if( power[0] == power[1] ) {
	  power[0] = power[1] = 0;
	  continue;
	} 
	if( power[0] < power[1] ) {
	  puts("Nakajima-kun");
	  return;
	} else {
	  puts("Isono-kun");
	  return;
	}
      } else if( opr[0][i] == ATK ) {
	if( opr[1][i] == CHG ) {
	  puts("Isono-kun");
	  return;
	} else if( opr[1][i] == BLK ) {
	  if( power[0] == 5 ) {
	    puts("Isono-kun");
	    return;
	  } else {
	    power[0] = 0;
	  }
	}
      } else {
	if( opr[0][i] == CHG ) {
	  puts("Nakajima-kun");
	  return;
	} else if( opr[0][i] == BLK ) {
	  if( power[1] == 5 ) {
	    puts("Nakajima-kun");
	    return;
	  } else {
	    power[1] = 0;
	  }
	}
      }
    } else {
      if( opr[0][i] == CHG ) power[0] = min(5,power[0]+1);
      if( opr[1][i] == CHG ) power[1] = min(5,power[1]+1);
    }
  }
  puts("Hikiwake-kun");
}

int main() {
  cin >> n;
  rep(i,2) rep(j,n) {
    string tmp;
    cin >> tmp;
    if( tmp[0] == 'm' ) opr[i][j] = BLK;
    if( tmp[0] == 't' ) opr[i][j] = CHG;
    if( tmp[0] == 'k' ) opr[i][j] = ATK;
  }
  compute();
  return 0;
}

