int K;
string iso[101];
string naka[101];

string solve() {
    int pow1=0,pow2=0;
    rep(i,K) {
        if(iso[i]=="tameru") pow1++;
        if(naka[i]=="tameru") pow2++;
        pow1=min(pow1,5);
        pow2=min(pow2,5);

        if(iso[i]=="kougekida") {
            if(pow1!=0&&naka[i]=="tameru") {
                return "Isono-kun";
            }
            if(naka[i]=="kougekida") {
                if(pow1==0) {
                    if(pow2==0) continue;
                    else return "Nakajima-kun";
                }
                if(pow1==pow2) continue;
                if(pow2<pow1) return "Isono-kun";
                else return "Nakajima-kun";
            }
            if(naka[i]=="mamoru") {
                if(pow1==5) return "Isono-kun";
            }
            pow1=0;
        }

        if(naka[i]=="kougekida") {
            if(pow1!=0&&iso[i]=="tameru") {
                return "Nakajima-kun";
            }
            if(iso[i]=="kougekida") {
                if(pow2==0) {
                    if(pow1==0) continue;
                    else return "Isono-kun";
                }
                if(pow1==pow2) continue;
                if(pow2>pow1) return "Isono-kun";
                else return "Nakajima-kun";
            }
            if(iso[i]=="mamoru") {
                if(pow2==5) return "Nakajima-kun";
            }
            pow2=0;
        }
    }

    return "Hikiwake-kun";
}
int main() {
    cin>>K;
    rep(i,K) cin>>iso[i];
    rep(i,K) cin>>naka[i];
    cout<<solve()<<endl;
    return 0;
}

