typedef long long int lli;
typedef long double ld;

typedef tuple<int, int, int> Dat;
typedef int hand; // Mamoru, Tameru, Kougekida

int charge(int a, int hand) {
  if (a < 5 && hand == 1) {
    return a + 1 ;
  } else if (hand == 2) {
    return 0;
  } else {
    return a;
  }
}

Dat move(Dat d, hand ha, hand hb) {
  if (get<0>(d) != 0) return d;
  int a = get<1>(d), b = get<2>(d);
  if (ha == 2 and a == 0 and !(hb == 2 and b == 0)) return Dat(-1, 0, 0);
  if (hb == 2 and b == 0 and !(ha == 2 and a == 0)) return Dat(1, 0, 0);
  if (ha == 2 and hb == 2 and a > b) return Dat(1, 0, 0);
  if (ha == 2 and hb == 2 and a < b) return Dat(-1, 0, 0);
  if (ha == 1 and hb == 2) return Dat(-1, 0, 0);
  if (ha == 2 and hb == 1) return Dat(1, 0, 0);
  if (ha == 0 and hb == 2 and b == 5) return Dat(-1, 0, 0);
  if (ha == 2 and hb == 0 and a == 5) return Dat(1, 0, 0);
  a = charge(a, ha), b = charge(b, hb);
  return Dat(0, a, b);
}

int to_i(string s) {
  if (s == "mamoru") return 0;
  if (s == "tameru") return 1;
  else return 2;
}

int main() {
