  vector<int> I_(k), N_(k);
  REP(i, k) {
    string is; cin >> is;
    I_[i] = to_i(is);
  }
  REP(i, k) {
    string na; cin >> na;
    N_[i] = to_i(na);
  }

  Dat d(0, 0, 0);
  REP(i, k) {
    d = move(d, I_[i], N_[i]);
  }
  int sh = get<0>(d);
  if (sh == 0) cout << "Hikiwake-kun" << endl;
  if (sh == 1) cout << "Isono-kun" << endl;
  if (sh == -1) cout << "Nakajima-kun" << endl;
  return 0;
}

